import React, { useReducer } from 'react';
import TareaContext from './tareaContext';
import TareaReducer from './tareaReducer';
import {
    TAREAS_PROYECTO,
    AGREGAR_TAREA,
    VALIDAR_TAREA,
    ELIMINAR_TAREA,
    ESTADO_TAREA,
    TAREA_ACTUAL
} from '../../types';

const TareaState = props => {
    const initialState = {
        tareas : [
            {id: 1, proyectoId: 1, nombre: 'Elegir Plataforma', estado: true},
            {id: 2, proyectoId: 2, nombre: 'Elegir Colores', estado: false},
            {id: 3, proyectoId: 2, nombre: 'Elegir Plataformas de Pago', estado: false},
            {id: 4, proyectoId: 3, nombre: 'Elegir Hosting', estado: true},
        ],
        tareasproyecto: null,
        errortarea: false,
    }
    // Crear dispatch y state
    const [state, dispatch] = useReducer(TareaReducer, initialState);

    //crear las funciones


    //Obtener las tareas de un proyecto
    const obtenerTareas = proyectoId => {
        dispatch ({
            type: TAREAS_PROYECTO,
            payload: proyectoId,
        })
    }

    //Agregar Tarea
    const agregarTarea = tarea => {
        dispatch({
            type: AGREGAR_TAREA,
            payload: tarea,
        })
    }

    //Validar tarea
    const validarTarea = () => {
        dispatch({
            type: VALIDAR_TAREA,
        })
    }

    //Eliminar tarea por id
    const eliminarTarea = id => {
        dispatch({
            type: ELIMINAR_TAREA,
            payload: id,
        })
    }

    //Cambia el estado de cada tarea
    const cambiarEstadoTarea = tarea => {
        dispatch({
            type: ESTADO_TAREA,
            payload: tarea,
        })
    }

    return (
        <TareaContext.Provider
            value={{
                tareas: state.tareas,
                tareasproyecto : state.tareasproyecto,
                errortarea: state.errortarea,
                agregarTarea,
                obtenerTareas,
                validarTarea,
                eliminarTarea,
                cambiarEstadoTarea,
            }}
        >
            {props.children}
        </TareaContext.Provider>
    )
}

export default TareaState;
