import React, { useReducer } from 'react';
import TareaContext from './tareaContext';
import TareaReducer from './tareaReducer';

const TareaState = props => {
    const initialState = {
        tareas: [
            {proyectoId: 1, nombre: 'Elegir Plataforma', estado: true},
            {proyectoId: 2, nombre: 'Elegir Colores', estado: false},
            {proyectoId: 2, nombre: 'Elegir Plataformas de Pago', estado: false},
            {proyectoId: 3, nombre: 'Elegir Hosting', estado: true},
        ],
    }
    // Crear dispatch y state
    const [state, dispatch] = useReducer(TareaReducer);
    return (
        <TareaContext.Provider
            value={{
                tareas: state.tareas
            }}
        >
            {props.children}
        </TareaContext.Provider>
    )
}

export default TareaState;
