import React, { useReducer } from 'react';
import TareaContext from './tareaContext';
import TareaReducer from './tareaReducer';
import {
    TAREAS_PROYECTO,
    AGREGAR_TAREA,
} from '../../types';

const TareaState = props => {
    const initialState = {
        tareas : [
            {proyectoId: 1, nombre: 'Elegir Plataforma', estado: true},
            {proyectoId: 2, nombre: 'Elegir Colores', estado: false},
            {proyectoId: 2, nombre: 'Elegir Plataformas de Pago', estado: false},
            {proyectoId: 3, nombre: 'Elegir Hosting', estado: true},
        ],
        tareasproyecto: null,
        errortarea: false,
    }
    // Crear dispatch y state
    const [state, dispatch] = useReducer(TareaReducer, initialState);

    //crear las funciones


    //Obtener las tareas de un proyecto
    const obtenerTareas = proyectoId => {
        dispatch ({
            type: TAREAS_PROYECTO,
            payload: proyectoId,
        })
    }

    //Agregar Tarea
    const agregarTarea = tarea => {
        dispatch({
            type: AGREGAR_TAREA,
            payload: tarea,
        })
    }

    return (
        <TareaContext.Provider
            value={{
                tareas: state.tareas,
                tareasproyecto : state.tareasproyecto,
                errortarea: state.errortarea,
                obtenerTareas,
            }}
        >
            {props.children}
        </TareaContext.Provider>
    )
}

export default TareaState;
